import { useState, useEffect } from 'react';
import axios from 'axios';
import Card, { CardHeader, CardTitle, CardContent } from '../components/ui/Card';
import { ArrowUpRight, ArrowDownLeft, MessageCircle } from 'react-icons/ai';

function ActivityHistory() {
  const [history, setHistory] = useState([]);
  const userId = localStorage.getItem('userId');

  useEffect(() => {
    const fetchHistory = async () => {
      try {
        const token = localStorage.getItem('token');
        const response = await axios.get(`http://localhost:5001/api/appreciation/history/${userId}`, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        setHistory(response.data);
      } catch (error) {
        console.error('Error fetching appreciation history:', error);
      }
    };

    fetchHistory();
  }, [userId]);

  return (
    <div className="container mx-auto mt-20 p-4">
      <Card>
        <CardHeader>
          <CardTitle className="text-2xl font-bold mb-6">Activity History</CardTitle>
        </CardHeader>
        <CardContent>
          {history.length > 0 ? (
            <div className="space-y-4">
              {history.map((entry) => (
                <div key={entry.id} className="border-b pb-4 last:border-b-0">
                  <div className="flex justify-between items-center mb-3">
                    <div className="flex items-center gap-2">
                      {entry.sender_id === parseInt(userId) ? (
                        <ArrowUpRight className="text-blue-500" />
                      ) : (
                        <ArrowDownLeft className="text-green-500" />
                      )}
                      <p className="font-medium">
                        {entry.sender_id === parseInt(userId) ? `To: ${entry.recipient_username}` : `From: ${entry.sender_username}`}
                      </p>
                    </div>
                    <div className="flex items-center gap-4">
                      <p className={`font-medium ${entry.sender_id === parseInt(userId) ? 'text-blue-600' : 'text-green-600'}`}>
                        ${entry.amount.toFixed(2)}
                      </p>
                      <p className="text-sm text-gray-500">{entry.created_at}</p>
                    </div>
                  </div>
                  <div className="bg-gray-50 p-4 rounded-lg">
                    <div className="flex items-start gap-2">
                      <MessageCircle size={18} className="text-gray-400 mt-1" />
                      <p className="text-gray-700">{entry.message}</p>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          ) : (
            <p className="text-center text-gray-600">No appreciation history available.</p>
          )}
        </CardContent>
      </Card>
    </div>
  );
}

export default ActivityHistory;
